import React from 'react'
import { connect } from 'react-redux'

import { 
    deleteFromBasket, 
    addProduct,
    deleteProduct
} from 'reducers/basket'

function BasketItem(props) {
    const { id, name, price, count, deleteFromBasket, addProduct, deleteProduct } = props
    return (
        <li className="collection-item">
            { name } 
            <i className='material-icons basket-count' onClick={() => deleteProduct(id)}>remove</i> Ã— 
            <i className='material-icons basket-count' onClick={() => addProduct(id)}>add</i> { count } = { price * count }
            <span>
                <i className="material-icons">plus</i>
            </span>
            <span className="secondary-content">
                <i onClick={() => deleteFromBasket(id)} className="material-icons basket-delete">close</i>
            </span>
        </li>
    )
}

let mapDispatchToProps = {
    deleteFromBasket,
    addProduct,
    deleteProduct
}

export default connect(null, mapDispatchToProps)(BasketItem)
