import React from 'react'
import { connect } from 'react-redux'

import BasketItem from 'components/Basket/BasketList/BasketItem'
import { 
    getOrders, 
    getSumAllOrders 
} from 'selectors'
import { 
    changeVisible,
    sendOrder
} from 'reducers/basket'

function BasketList({ orders, result = 0, changeVisible, sendOrder }) {
    console.log(orders)
    return (
        <ul className="collection basket-list">
            <li className="collection-item active">Корзина</li>
            { orders.length ?
                orders.map(order => <BasketItem key={order.id} {...order} />)
            :
                <li className="collection-item">Корзина пуста</li>
            }
            <li className="collection-item active">Сумма заказа: {result}
                <button disabled={orders.length === 0} onClick={() => sendOrder(orders)} className='right basket-btn-pay'>Оплатить</button>
            </li>
            <i className='material-icons basket-close' onClick={changeVisible}>close</i>
        </ul>
    )
}

let mapStateToProps = state => {
    return {
        orders: getOrders(state),
        result: getSumAllOrders(state)
    }
},
mapDispatchToProps = {
    changeVisible,
    sendOrder
}

export default connect(mapStateToProps, mapDispatchToProps)(BasketList)
