const GET_NAME_FOR_ALERT = 'GET_NAME_FOR_ALERT'
const CLOSE_ALERT = 'CLOSE_ALERT'

let initialState = {
    name: ''
}

export const alert = (state = initialState, {type, payload}) => {
    switch (type) {
        case GET_NAME_FOR_ALERT:
            return {
                ...state,
                name: payload
            }
        case CLOSE_ALERT:
            return {
                ...state,
                name: ''
            }
        default:
            return state
    }
}

export const getNameForAlert = name => dispatch => {
    dispatch({
        type: GET_NAME_FOR_ALERT,
        payload: name
    })
}

export const closeAlert = () => dispatch => {
    dispatch({
        type: CLOSE_ALERT
    })
}